-- Name: can_view_private_fields(public.proposals); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.can_view_private_fields(proposal_row public.proposals) RETURNS boolean
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
BEGIN
  -- Proposal owner can always view private fields
  IF proposal_row.user_id = auth.uid() THEN
    RETURN TRUE;
  END IF;
  
  -- Check if user has signed NDA for this proposal
  IF EXISTS (
    SELECT 1 FROM proposal_nda_signatures 
    WHERE proposal_id = proposal_row.id 
    AND signer_user_id = auth.uid()
  ) THEN
    RETURN TRUE;
  END IF;
  
  RETURN FALSE;
END;
$$;


