--
-- Name: nda_requests_summary; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.nda_requests_summary AS
 SELECT pns.id,
    pns.proposal_id,
    pns.signer_user_id,
    pns.status,
    pns.request_message,
    pns.pending_at,
    pns.approved_at,
    pns.rejected_at,
    pns.rejection_reason,
    pns.approved_by_user_id,
    p.title AS proposal_title,
    p.user_id AS proposal_owner_id,
    u_signer.name AS signer_name,
    u_signer.email AS signer_email,
    u_signer.avatar_url AS signer_avatar,
    u_approver.name AS approver_name,
    count(nrf.id) AS attached_files_count
   FROM ((((public.proposal_nda_signatures pns
     JOIN public.proposals p ON ((p.id = pns.proposal_id)))
     JOIN public.users u_signer ON ((u_signer.id = pns.signer_user_id)))
     LEFT JOIN public.users u_approver ON ((u_approver.id = pns.approved_by_user_id)))
     LEFT JOIN public.nda_request_files nrf ON ((nrf.nda_signature_id = pns.id)))
  GROUP BY pns.id, pns.proposal_id, pns.signer_user_id, pns.status, pns.request_message, pns.pending_at, pns.approved_at, pns.rejected_at, pns.rejection_reason, pns.approved_by_user_id, p.title, p.user_id, u_signer.name, u_signer.email, u_signer.avatar_url, u_approver.name;


